      Posts.hasMany(models.Comments, { foreignKey: "postId", as: "Comments" });
    }
  }
  Posts.init(
    {
      id: {
        type: DataTypes.UUID,
        allowNull: false,
        primaryKey: true,
        defaultValue: sequelize.fn("uuid_generate_v4"),
      },
