import React from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Divider from "@mui/material/Divider";
import UploadFileIcon from "@mui/icons-material/UploadFile";


function CreateArticle() {
  return (
    <div>
      <h1 style={{ color: "blue", fontFamily: "Poppins" }}>Create New Article</h1>
      <Divider />

      <label>Give it a title</label>
      <br />
      <TextField id="outlined-basic" />
      <br />

      <label>Min. to read it</label>
      <br />

      <TextField id="outlined-basic" />
      <br />
      <br />


      <label>Write something about it</label>
      <br />

      <TextField id="outlined-basic" />
      <br />
      <br />

      <Button
        component="label"
        variant="outlined"
        startIcon={<UploadFileIcon />}
        sx={{ marginRight: "1rem" }}
      >
        Browse
        <input type="file" accept=".png" hidden onChange={handleFileUpload} />
      </Button>


      <Button variant="contained" color="primary">
        Save Changes
      </Button>
    </div>
  );
}

export default CreateArticle;
