import styles from '../styles/Login/Login.module.css';
import OutlinedInput from '@mui/material/OutlinedInput';
import Container from '@mui/material/Container';
import { useForm, Controller } from 'react-hook-form';
import Button from '@mui/material/Button';
import React,{useState} from 'react';
import Divider from '@mui/material/Divider';
import { Link } from 'react-router-dom';
import FormLabel from '@mui/material/FormLabel';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import IconButton from '@mui/material/IconButton';
import InputAdornment from '@mui/material/InputAdornment';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';
import { AppContext } from '../App';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from 'yup';
 import {  useNavigate } from "react-router-dom";
import {useContext} from "react";
import { getLoginDetails, parseJwt } from '../services/LoginApi';
const schema = yup
  .object({
    email: yup.string().email().required(),
    password: yup
      .string()
      .required()
      .matches(
        //.min(4).max(15)
        /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{8,}$/,
        'Must Contain 8 Characters, One Uppercase, One Lowercase, One Number and one special case Character'
      ),
    //.matches(/^[!@#$%^&*(),.?":{}|<>]+$/, " Must contain a special character"),//.matches( /^[!@#$%^&*(),.?":{}|<>]+$/, "special character "),
  })
  .required();

function Login() {
  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm({
    defaultValues: {
      email: '',
      password: '',
    },
    resolver: yupResolver(schema),
  });

  const { parentTransfer,userToken } = useContext(AppContext);
  const [message,setMessage] = useState(false);
  const navigate = useNavigate();
  // const login=()=>{
  //   localStorage.setItem('login', true);
  // }
  // const navigate = useNavigate();

  // useEffect(() => {
  //   // let login = localStorage.getItem('login');
  //   // if(login){
  //   //     navigate('/');
  //   // }
  // });
  const onSubmit = async(data) => {
    const response = await getLoginDetails(data);
    if(response.data.accessToken) {
      userToken(response.data.accessToken)
      const parsetoken = parseJwt(response.data.accessToken)
      parentTransfer(parsetoken.user);
      navigate('/create-article');
    }else{
        setMessage(true);
    }
  };

  const [values, setValues] = React.useState({
    showPassword: false,
  });

  const handleClickShowPassword = () => {
    setValues({
      showPassword: !values.showPassword,
    });
  };

  const handleMouseDownPassword = (event) => {
    event.preventDefault();
  };

  return (
    <Container maxWidth="sm">
      {message && <p style={{color: "red"}}>Wrong Credentials</p>}
      <h1 className={styles.headingOne}>Log In</h1>

      <form onSubmit={handleSubmit(onSubmit)}>
        <FormLabel htmlFor="my-input">Email address or username</FormLabel>{' '}
        <br />
        <br />
        <Controller
          control={control}
          name="email"
          rules={{ required: true }}
          render={({
            field: { onChange, onBlur, value, name, ref },
            fieldState: { invalid, isTouched, isDirty, error },
            formState,
          }) => (
            <OutlinedInput
            variant="outlined"
              color="secondary"
              onBlur={onBlur} // notify when input is touched
              onChange={onChange} // send value to hook form
              checked={value}
              inputRef={ref}
              sx={{
                borderRadius: 18,
                width: 550,
                marginBottom: 3,
              }}
              // fullWidth
              variant="outlined"
            />
          )}
        />
        {errors.email && <p>{errors.email.message}</p>}
        <br />
        <FormLabel htmlFor="my-input">Password</FormLabel>
        <br />
        <br />
        <Controller
          control={control}
          name="password"
          rules={{ required: true }}
          render={({
            field: { onChange, onBlur, value, name, ref },
            fieldState: { invalid, isTouched, isDirty, error },
            formState,
          }) => (
            <OutlinedInput
              type={values.showPassword ? 'text' : 'password'}
              value={values.password}
              onChange={onChange} // send value to hook form
              sx={{
                borderRadius: 18,
                width: 550,

              }}
//              fullWidth
              endAdornment={
                <InputAdornment position="end">
                  <IconButton
                    aria-label="toggle password visibility"
                    onClick={handleClickShowPassword}
                    onMouseDown={handleMouseDownPassword}
                    edge="end"
                  >
                    {values.showPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              }
              label="Password"
            />
          )}
        />
        {errors.password && <p>{errors.password.message}</p>}
        <Link to="/change-password">
          <h5 className={styles.headingFive}>Forgot your password?</h5>
        </Link>
        <FormControlLabel
          control={<Checkbox color="secondary" />}
          label="Remember Me"
          sx={{ marginBottom: 2 , }}
        />
        <Button
          type="submit"
          variant="contained"
          color="secondary"
          fullWidth
          sx={{ borderRadius: 25, fontSize: '22px' }}
        >
          Log in
        </Button>
      </form>

      <br />
      <br />
      <Divider />

      <h3 className={styles.h3}>Don't have an account?</h3>

      <Link to="/signup">
        <Button
          fullWidth
          variant="outlined"
          color="secondary"
          // onClick = {login}
          sx={{ borderRadius: '25px', fontSize: '22px' }}
        >
          Sign up
        </Button>
      </Link>
      <br />
    </Container>
  );
}

export default Login;
