import {
  Avatar,
  Card,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Typography,
} from '@mui/material';
import { Box } from '@mui/system';
import React from 'react';

const Comment = () => {
  return (
    <Card
      elevation={10}
      sx={{ display: 'flex', allignItems: 'centre', marginTop: '20px' }}
    >
      <Box>
        <List>
          <ListItem>
            <ListItemIcon>
              <Avatar url="" alt="user-avatar"></Avatar>
            </ListItemIcon>
            <ListItemText sx={{ marginRight: '10px' }}>spongebob</ListItemText>
            <ListItemText>3 Min Ago</ListItemText>
          </ListItem>
        </List>
      </Box>
      <Typography sx={{ fontFamily: '' }}>
        Did you come here for something in particular or just general
        Riker-bashing? And blowing into maximum
      </Typography>
    </Card>
  );
};

export default Comment;
